var pollRequest=new Request.JSON({method:"post",url:ecrAJAXLink+"&controller=logfiles&task=pollLog",initialDelay:100,delay:300,limit:15000,onRequest:function(){document.id("pollStatus").set("text","running...");var a=document.id("ecrProgressBar");if(null!=a){a.getParent().addClass("active")}},onSuccess:function(a){var c=document.id("ecrDebugBox");var b=document.id("ecrProgressBar");if(null!=b){b.setStyle("width",a.progress+"%")}c.set("html",a.message.replace(/([^>\r\n]?)(\r\n|\n\r|\r|\n)/g,"$1<br />$2"));c.scrollTop=c.scrollHeight},onFailure:function(){document.id("pollStatus").set("text","Sorry, your request failed :(")}});function startPoll(){pollRequest.startTimer()}function stopPoll(){pollRequest.stopTimer();document.id("pollStatus").set("text","idle")};